int hammingDistance(String dna1, String dna2) {
  bool equalLength = dna1.length == dna2.length;
  int counter = 0;

  if (equalLength) {
    for (var i = 0; i < dna1.length; i++) {
      dna1[i] != dna2[i] ? counter++ : counter;
    }
  }
  return counter;
}

dynamic approximateOccurences(
    String pattern, String genome, int mismatchThreshold) {
  int windowsLength = pattern.length;
  Map<String, dynamic> memo = {};
  for (int i = 0; i < (genome.length - windowsLength + 1); i++) {
    String currentSlidingWindow = genome.substring(i, i + windowsLength);
    if (memo.containsKey(currentSlidingWindow)) {
      memo[currentSlidingWindow]["index"].add(i);
    } else {
      memo[currentSlidingWindow] = {
        "hammingDistance": hammingDistance(currentSlidingWindow, pattern),
        "index": [i]
      };
    }
  }

  var filtered = memo.values.where(
    (element) => element["hammingDistance"] <= mismatchThreshold,
  );
  return filtered.fold([], (previousValue, element) => [...previousValue, ...element["index"]],)..sort();
//   return memo;
}
