String string1 = "";
String string2 = "";

void main() async {
  print(hammingDistance(string1, string2));
}

dynamic hammingDistance(String dna1, String dna2) {
  bool equalLength = dna1.length == dna2.length;
  int counter = 0;
  
  if (equalLength) {
    for (var i = 0; i < dna1.length; i++) {
      dna1[i] != dna2[i] ? counter++ : counter;
    }
  } else {
    return null;
  }
  
  return counter;
}
